# ==========================================
# 3. æ•¸æ“šæ¸…æ´—èˆ‡æ¨™è¨»é‚è¼¯
# ==========================================
def process_novel(text):
    # A. ç°¡å–®åˆ†å¥
    sentences = re.split(r'([ã€‚ï¼ï¼Ÿ\n])', text)
    segments = []
    current_sent = ""
    for s in sentences:
        current_sent += s
        if re.match(r'[ã€‚ï¼ï¼Ÿ\n]', s):
            if current_sent.strip():
                segments.append(current_sent.strip())
            current_sent = ""
    if current_sent.strip(): segments.append(current_sent.strip())

    # Label ID ç¢ºä¿èˆ‡ master list ä¸€è‡´ (å¯é¸)
    label2id = {"O": 0, "B-NAME": 1, "I-NAME": 2, "B-ORG": 13, "I-ORG": 14} 
    final_data = []

    for sent in segments:
        tokens = smart_tokenize(sent)
        tags = [0] * len(tokens) # é è¨­å…¨ç‚º O (ID=0)
        
        # ğŸ”¥ é—œéµä¿®å¾©ï¼šå»ºç«‹ Token ä½ç½®æ˜ å°„
        token_spans = []
        search_start = 0
        for token in tokens:
            start = sent.find(token, search_start)
            if start == -1:
                token_spans.append(None)
                continue
            end = start + len(token)
            token_spans.append((start, end))
            search_start = end # ç¢ºä¿å‘å‰æœå°‹

        # æ¨™è¨»é‚è¼¯
        for name in target_names:
            for match in re.finditer(re.escape(name), sent):
                match_start, match_end = match.span()
                
                for idx, span in enumerate(token_spans):
                    if span is None: continue
                    t_start, t_end = span
                    
                    # æª¢æŸ¥ Token æ˜¯å¦åœ¨åå­—ç¯„åœå…§
                    if t_start >= match_start and t_end <= match_end:
                        if t_start == match_start:
                            # âœ… Best Practice: é˜²æ­¢è¦†è“‹ (å¦‚æœå·²ç¶“æœ‰æ¨™ç±¤ï¼Œå°±ä¸æ”¹)
                            if tags[idx] == 0: 
                                tags[idx] = label2id["B-NAME"]
                        else:
                            # âœ… Best Practice: é˜²æ­¢è¦†è“‹
                            if tags[idx] == 0:
                                tags[idx] = label2id["I-NAME"]
        
        if len(tokens) > 0:
            final_data.append({"tokens": tokens, "ner_tags": tags})
            
    return final_data

# ==========================================
# 4. åŸ·è¡Œèˆ‡å„²å­˜
# ==========================================
novel_json_data = process_novel(raw_content)

output_file = "novel_data.json"
with open(output_file, "w", encoding="utf-8") as f:
    json.dump(novel_json_data, f, ensure_ascii=False, indent=2)

print(f"âœ… è™•ç†å®Œæˆï¼å…±ç”Ÿæˆ {len(novel_json_data)} æ¢å·²æ¨™è¨»æ•¸æ“šã€‚")
print(f"ğŸ“ æª”æ¡ˆå·²å„²å­˜ç‚º: {output_file}")